CREATE OR REPLACE PROCEDURE calculate_max_from_blob (p_blob IN BLOB, p_max OUT FLOAT) IS\n  v_data RAW(32767);\n  v_float FLOAT;\n  v_max FLOAT := -1e308; -- initialize to a very low value\n  v_pos INTEGER := 1;\n  v_length INTEGER;\nBEGIN\n  v_length := DBMS_LOB.getlength(p_blob);\n  WHILE v_pos <= v_length LOOP\n    DBMS_LOB.read(p_blob, 4, v_pos, v_data); -- Reading 4 bytes for each float\n    v_float := UTL_RAW.cast_to_binary_float(v_data); -- Convert RAW to float\n    IF v_float > v_max THEN\n      v_max := v_float;\n    END IF;\n    v_pos := v_pos + 4;\n  END LOOP;\n  p_max := v_max;\nEND calculate_max_from_blob;\n/