SET SERVEROUTPUT ON\nDECLARE\n  CURSOR profile_cursor IS\n    SELECT \n      papu.profile_option_name,\n      papu.profile_option_id,\n      fnd_profile.value_specific(papu.profile_option_name, 'SITE') AS site_value,\n      fnd_profile.value_specific(papu.profile_option_name, 'APPLICATION') AS application_value,\n      fnd_profile.value_specific(papu.profile_option_name, 'RESPONSIBILITY') AS responsibility_value,\n      fnd_profile.value_specific(papu.profile_option_name, 'USER') AS user_value\n    FROM \n      fnd_profile_options_vl papu;\nBEGIN\n  DBMS_OUTPUT.PUT_LINE('Profile Option Name | Site Value | Application Value | Responsibility Value | User Value');\n  DBMS_OUTPUT.PUT_LINE('--------------------------------------------------------------------------------');\n  FOR profile_rec IN profile_cursor LOOP\n    DBMS_OUTPUT.PUT_LINE(profile_rec.profile_option_name || ' | ' ||\n                         NVL(profile_rec.site_value, 'NULL') || ' | ' ||\n                         NVL(profile_rec.application_value, 'NULL') || ' | ' ||\n                         NVL(profile_rec.responsibility_value, 'NULL') || ' | ' ||\n                         NVL(profile_rec.user_value, 'NULL'));\n  END LOOP;\nEND;\n/