DECLARE\n    CURSOR c1 IS \n        SELECT rowid, my_column \n        FROM my_table \n        WHERE my_column IS NOT NULL;\n        \n    v_sql_errm  VARCHAR2(2048);\n    v_rowid     ROWID;\n    v_my_column my_table.my_column%TYPE;\nBEGIN\n    FOR r IN c1 LOOP\n        BEGIN\n            v_rowid := r.rowid;\n            v_my_column := r.my_column;\n            \n            UPDATE my_table \n            SET my_column = do_something(v_my_column)\n            WHERE rowid = v_rowid;\n            \n        EXCEPTION\n            WHEN OTHERS THEN\n                v_sql_errm := SQLERRM;\n                INSERT INTO log_error (msg) \n                VALUES ('Error updating value for ROWID ' || v_rowid || \n                        ' with value ' || v_my_column || ': ' || v_sql_errm);\n        END; -- End of inner BEGIN/EXCEPTION block\n    END LOOP;\nEND;