DECLARE\n    v_data table1.column%TYPE;\nBEGIN\n    BEGIN\n        SELECT data INTO v_data FROM table1 WHERE <criteria>;\n    EXCEPTION\n        WHEN NO_DATA_FOUND THEN\n            BEGIN\n                SELECT data INTO v_data FROM table2 WHERE <criteria>;\n            EXCEPTION\n                WHEN NO_DATA_FOUND THEN\n                    SELECT data INTO v_data FROM table3 WHERE <criteria>;\n            END;\n    END;\n    \n    RETURN v_data;\nEND;