DECLARE\n    v_data table1.column%TYPE;\n    CURSOR c1 IS SELECT data FROM table1 WHERE <criteria>;\n    CURSOR c2 IS SELECT data FROM table2 WHERE <criteria>;\n    CURSOR c3 IS SELECT data FROM table3 WHERE <criteria>;\nBEGIN\n    OPEN c1;\n    FETCH c1 INTO v_data;\n    IF c1%FOUND THEN\n        CLOSE c1;\n        RETURN v_data;\n    ELSE\n        CLOSE c1;\n        OPEN c2;\n        FETCH c2 INTO v_data;\n        IF c2%FOUND THEN\n            CLOSE c2;\n            RETURN v_data;\n        ELSE\n            CLOSE c2;\n            OPEN c3;\n            FETCH c3 INTO v_data;\n            IF c3%FOUND THEN\n                CLOSE c3;\n                RETURN v_data;\n            ELSE\n                CLOSE c3;\n                -- Handle case where no data is found in any table\n                RETURN NULL;\n            END IF;\n        END IF;\n    END IF;\nEND;