DECLARE\n  l_ddl   CLOB;\n  l_file  UTL_FILE.FILE_TYPE;\n  l_start NUMBER := 1;\n  l_buffer_size CONSTANT NUMBER := 32767;\n  l_buffer VARCHAR2(32767);\nBEGIN\n  -- Get DDL for a specific procedure\n  l_ddl := DBMS_METADATA.GET_DDL('PROCEDURE', 'YOUR_PROCEDURE_NAME', 'YOUR_SCHEMA_NAME');\n  \n  -- Open the file for writing\n  l_file := UTL_FILE.FOPEN('BACKUP_DIR', 'your_procedure_name.sql', 'w', l_buffer_size);\n\n  -- Write the CLOB to the file in chunks\n  WHILE l_start <= DBMS_LOB.GETLENGTH(l_ddl) LOOP\n    l_buffer := DBMS_LOB.SUBSTR(l_ddl, l_buffer_size, l_start);\n    UTL_FILE.PUT_LINE(l_file, l_buffer);\n    l_start := l_start + l_buffer_size;\n  END LOOP;\n\n  -- Close the file\n  UTL_FILE.FCLOSE(l_file);\nEXCEPTION\n  WHEN OTHERS THEN\n    -- Ensure the file is closed in case of an error\n    IF UTL_FILE.IS_OPEN(l_file) THEN\n      UTL_FILE.FCLOSE(l_file);\n    END IF;\n    RAISE;\nEND;\n/