DECLARE\n  l_xml XMLType := XMLType('<root><element>value</element></root>');\n  l_varchar1 VARCHAR2(4000);\n  l_varchar2 VARCHAR2(4000);\nBEGIN\n  -- Convert XMLType to CLOB\n  l_clob := l_xml.getClobVal();\n\n  -- Split CLOB into VARCHAR2(4000) chunks\n  l_varchar1 := DBMS_LOB.SUBSTR(l_clob, 4000, 1);\n  l_varchar2 := DBMS_LOB.SUBSTR(l_clob, 4000, 4001);\n  \n  -- Call the remote procedure with the split chunks\n  -- EXECUTE IMMEDIATE 'BEGIN remote_procedure@dblink(:1, :2); END;' USING l_varchar1, l_varchar2;\nEND;