DECLARE\n    l_alias VARCHAR2(100);\n    l_sql   VARCHAR2(2000);\nBEGIN\n    -- Example alias with double quotes\n    l_alias := 'This "is" an alias';\n\n    -- Replace double quotes with two double quotes\n    l_alias := REPLACE(l_alias, '"', '""');\n\n    -- Build the dynamic SQL string\n    l_sql := 'SELECT table1.Col1 AS "' || l_alias || '" FROM table1';\n\n    -- Output the dynamic SQL for debugging\n    DBMS_OUTPUT.PUT_LINE(l_sql);\n    \n    -- Execute the dynamic SQL (if needed)\n    -- EXECUTE IMMEDIATE l_sql;\nEND;\n/