DECLARE\n      v_files_list webutil_file.file_list;\n      v_file_name VARCHAR2(255);\n      v_file_size NUMBER;\n      v_file_date DATE;\n   BEGIN\n      -- Initialize WebUtil\n      IF NOT webutil_file_transfer.is_directories_supported THEN\n         MESSAGE('WebUtil is not configured correctly.');\n         RAISE FORM_TRIGGER_FAILURE;\n      END IF;\n\n      -- Get the list of PDF files in the directory\n      v_files_list := webutil_file.file_list('C:\path\to\your\folder', 'PDF');\n\n      -- Clear the block\n      CLEAR_BLOCK;\n\n      -- Populate the block with file details\n      FOR i IN 1 .. v_files_list.count LOOP\n         v_file_name := v_files_list(i).filename;\n         v_file_size := webutil_file.file_size('C:\path\to\your\folder\' || v_file_name);\n         v_file_date := webutil_file.file_date('C:\path\to\your\folder\' || v_file_name);\n\n         -- Create a new record in the block\n         NEXT_RECORD;\n         :FILE_LIST_BLOCK.FILE_NAME := v_file_name;\n         :FILE_LIST_BLOCK.FILE_SIZE := v_file_size;\n         :FILE_LIST_BLOCK.FILE_DATE := v_file_date;\n      END LOOP;\n   END;