DECLARE\n    -- Declare constants for better readability\n    c_bonus_rate CONSTANT NUMBER := 0.10;\n    c_max_bonus CONSTANT NUMBER := 5000;\n\n    -- Variable to store employee salary\n    v_employee_salary NUMBER;\n    v_bonus NUMBER;\n\n    -- Cursor to fetch employee data\n    CURSOR c_employee IS\n        SELECT employee_id, salary FROM employees WHERE department_id = 10;\n\nBEGIN\n    -- Open cursor\n    OPEN c_employee;\n    \n    LOOP\n        -- Fetch employee data\n        FETCH c_employee INTO v_employee_salary;\n\n        -- Exit loop when no more rows\n        EXIT WHEN c_employee%NOTFOUND;\n        \n        -- Calculate bonus\n        v_bonus := v_employee_salary * c_bonus_rate;\n        \n        -- Cap the bonus to maximum allowed\n        IF v_bonus > c_max_bonus THEN\n            v_bonus := c_max_bonus;\n        END IF;\n        \n        -- Display the result\n        DBMS_OUTPUT.PUT_LINE('Employee Salary: ' || v_employee_salary || ', Bonus: ' || v_bonus);\n    END LOOP;\n    \n    -- Close cursor\n    CLOSE c_employee;\nEND;