DECLARE\n    TYPE mytable_type IS TABLE OF mytable%ROWTYPE;\n    mytable_data mytable_type;\nBEGIN\n    -- Populate the collection mytable_data\n    FOR i IN 1..1000 LOOP\n        mytable_data(i).column1 := ...;\n        mytable_data(i).column2 := ...;\n        -- Assign values to other columns\n    END LOOP;\n    \n    -- Use FORALL to bulk insert\n    FORALL i IN mytable_data.FIRST..mytable_data.LAST\n        INSERT INTO mytable VALUES mytable_data(i);\n    \n    COMMIT;\nEND;\n/