DECLARE\n  v_blob      BLOB;\n  v_bfile     BFILE;\n  v_filename  VARCHAR2(100) := 'yourfile.ext';  -- Replace with your file name\nBEGIN\n  -- Initialize the BLOB\n  DBMS_LOB.CREATETEMPORARY(v_blob, TRUE);\n\n  -- Point to the file in the directory\n  v_bfile := BFILENAME('MY_DIR', v_filename);\n\n  -- Open the BFILE\n  DBMS_LOB.FILEOPEN(v_bfile, DBMS_LOB.FILE_READONLY);\n\n  -- Load the contents of the file into the BLOB\n  DBMS_LOB.LOADFROMFILE(v_blob, v_bfile, DBMS_LOB.GETLENGTH(v_bfile));\n\n  -- Close the BFILE\n  DBMS_LOB.FILECLOSE(v_bfile);\n\n  -- Now you can use v_blob as needed, for example, insert it into a table\n  INSERT INTO your_table (blob_column) VALUES (v_blob);\n\n  -- Free temporary BLOB\n  DBMS_LOB.FREETEMPORARY(v_blob);\n  \nEXCEPTION\n  WHEN OTHERS THEN\n    -- Handle exceptions\n    IF DBMS_LOB.FILEISOPEN(v_bfile) THEN\n      DBMS_LOB.FILECLOSE(v_bfile);\n    END IF;\n    DBMS_LOB.FREETEMPORARY(v_blob);\n    RAISE;\nEND;\n/