DECLARE\n    c_kilometers NUMBER := 0;\n    temp_kilometers NUMBER;\n    CURSOR cursor_t IS\n        SELECT code_att, code_aff FROM your_cursor_table;\nBEGIN\n    FOR l IN cursor_t LOOP\n        -- First SELECT statement\n        SELECT t.kilometers INTO temp_kilometers\n        FROM table_name t\n        WHERE t.code = l.code_att;\n\n        c_kilometers := c_kilometers + temp_kilometers;\n\n        -- Second SELECT statement\n        SELECT g.kilometers INTO temp_kilometers\n        FROM table_name g\n        WHERE g.code = l.code_aff;\n\n        c_kilometers := c_kilometers + temp_kilometers;\n    END LOOP;\n    DBMS_OUTPUT.PUT_LINE('Total kilometers: ' || c_kilometers);\nEND;\n/