DECLARE\n    file_handle UTL_FILE.FILE_TYPE;\n    file_name VARCHAR2(100) := 'example_output.txt';\n    file_path VARCHAR2(100) := '/path/to/directory';\nBEGIN\n    file_handle := UTL_FILE.FOPEN(file_path, file_name, 'w');\n\n    UTL_FILE.PUT_LINE(file_handle, 'This is a line of text.');\n    UTL_FILE.PUT_LINE(file_handle, 'This is another line of text, and it can be much longer than 256 characters without any issues.');\n\n    UTL_FILE.FCLOSE(file_handle);\nEXCEPTION\n    WHEN OTHERS THEN\n        IF UTL_FILE.IS_OPEN(file_handle) THEN\n            UTL_FILE.FCLOSE(file_handle);\n        END IF;\n        RAISE;\nEND;