DECLARE\n  -- your variable declarations\nBEGIN\n  outer_loop:\n  FOR i IN 1..10 LOOP\n    -- your outer loop logic\n    \n    IF some_condition THEN\n      -- your first IF logic\n      \n      IF nested_condition THEN\n        -- your nested IF logic\n        \n        -- Simulated CONTINUE\n        GOTO continue_outer_loop;\n      END IF;\n      \n      -- more logic here if the nested IF does not trigger the CONTINUE\n    END IF;\n    \n    <<continue_outer_loop>>\n    NULL;  -- continue here\n    \n    -- more logic for the outer loop\n  END LOOP outer_loop;\nEND;\n/