CREATE OR REPLACE PROCEDURE update_security_training_question(v_qstns_to_delete IN VARCHAR2) IS\n    TYPE num_array IS TABLE OF NUMBER;\n    l_qstns num_array;\nBEGIN\n    -- Split the string into individual numbers\n    SELECT TO_NUMBER(REGEXP_SUBSTR(v_qstns_to_delete, '[^,]+', 1, LEVEL))\n    BULK COLLECT INTO l_qstns\n    FROM dual\n    CONNECT BY REGEXP_SUBSTR(v_qstns_to_delete, '[^,]+', 1, LEVEL) IS NOT NULL;\n\n    -- Update the table using the collected numbers\n    FORALL i IN INDICES OF l_qstns\n        UPDATE org.security_training_question a\n        SET a.actv_indr = 'N'\n        WHERE a.qstn_id = l_qstns(i);\nEND;\n/