CREATE OR REPLACE TYPE some_table_type IS OBJECT (\n  column1 NUMBER,\n  column2 VARCHAR2(50)\n);\n/\n\nCREATE OR REPLACE TYPE some_table_tab IS TABLE OF some_table_type;\n/\n\nCREATE OR REPLACE FUNCTION get_data RETURN some_table_tab PIPELINED IS\nBEGIN\n  FOR rec IN (SELECT column1, column2 FROM some_table) LOOP\n    PIPE ROW(some_table_type(rec.column1, rec.column2));\n  END LOOP;\n  RETURN;\nEND get_data;\n/