DECLARE\n    v_owner       VARCHAR2(128) := 'SCHEMA_USER';\n    v_tablename   VARCHAR2(128) := 'TABLENAME';\n    v_sql         VARCHAR2(4000);\n    v_cursor      SYS_REFCURSOR;\n    TYPE t_rowtype IS RECORD (column1 datatype1, column2 datatype2, ...); -- Adjust with actual column names and datatypes\n    TYPE t_tab IS TABLE OF t_rowtype;\n    v_tab         t_tab;\nBEGIN\n    v_sql := 'SELECT * FROM ' || v_owner || '.' || v_tablename;\n    \n    OPEN v_cursor FOR v_sql;\n    \n    FETCH v_cursor BULK COLLECT INTO v_tab;\n    \n    CLOSE v_cursor;\n    \n    -- Now you can work with v_tab which holds the data from the specified table\n    FOR i IN 1..v_tab.COUNT LOOP\n        DBMS_OUTPUT.PUT_LINE(v_tab(i).column1 || ' ' || v_tab(i).column2); -- Adjust with actual column names\n    END LOOP;\nEND;\n/