CREATE OR REPLACE PROCEDURE GetNames (\n    prmName IN VARCHAR2,\n    prmSortField IN VARCHAR2,\n    prmSortDirection IN VARCHAR2\n) AS\n    query_str VARCHAR2(1000);\n    v_cur SYS_REFCURSOR;\nBEGIN\n    -- Construct the query with bind variables for the parameters\n    query_str := 'SELECT * FROM Names WHERE Name = :prmName ORDER BY ' || prmSortField || ' ' || prmSortDirection;\n\n    -- Open a cursor for the dynamic query\n    OPEN v_cur FOR query_str USING prmName;\n\n    -- Fetch and process the results as needed\n    -- For example, you can loop through the cursor or fetch rows into a record\n\n    CLOSE v_cur;\nEND;\n/