DECLARE\n    l_value PLS_INTEGER := 16; -- Example value\n    l_left_shifted PLS_INTEGER;\n    l_right_shifted PLS_INTEGER;\nBEGIN\n    l_left_shifted := bitwise_left_shift(l_value, 2); -- Equivalent to l_value << 2\n    l_right_shifted := bitwise_right_shift(l_value, 2); -- Equivalent to l_value >> 2\n\n    DBMS_OUTPUT.PUT_LINE('Left Shifted Value: ' || l_left_shifted); -- Output: 64\n    DBMS_OUTPUT.PUT_LINE('Right Shifted Value: ' || l_right_shifted); -- Output: 4\nEND;