DECLARE\n    v_sql VARCHAR2(4000);\nBEGIN\n    -- Retrieve the column names from the data dictionary\n    SELECT 'SELECT ' || LISTAGG(COLUMN_NAME, ', ') WITHIN GROUP (ORDER BY COLUMN_ID) || \n           ', TO_CHAR(CHANGE_DATE, ''YYYY-MM-DD HH24:MI:SS'') AS FORMATTED_DATE FROM MY_TABLE'\n    INTO v_sql\n    FROM USER_TAB_COLUMNS\n    WHERE TABLE_NAME = 'MY_TABLE';\n\n    -- Execute the dynamically constructed SQL statement\n    EXECUTE IMMEDIATE v_sql;\nEND;