DECLARE\n    var NUMBER;\n    otherVar NEEDED_FIELD_TYPE;\nBEGIN\n    SELECT COUNT(*) INTO var FROM table_name WHERE condition;\n    IF var > 0 THEN\n        SELECT NEEDED_FIELD INTO otherVar FROM table_name WHERE condition;\n        -- Further processing\n    ELSE\n        -- Handle no data found\n    END IF;\nEXCEPTION\n    WHEN OTHERS THEN\n        -- Handle other exceptions\nEND;