CREATE OR REPLACE TRIGGER compound_trigger_example\n   FOR INSERT OR UPDATE OR DELETE ON employees\n   COMPOUND TRIGGER\n      TYPE t_emp_ids IS TABLE OF employees.employee_id%TYPE;\n      l_emp_ids t_emp_ids;\n\n      BEFORE EACH ROW IS\n      BEGIN\n         IF INSERTING THEN\n            l_emp_ids.EXTEND;\n            l_emp_ids(l_emp_ids.LAST) := :NEW.employee_id;\n         END IF;\n      END BEFORE EACH ROW;\n\n      AFTER STATEMENT IS\n      BEGIN\n         FOR i IN l_emp_ids.FIRST .. l_emp_ids.LAST LOOP\n            DBMS_OUTPUT.PUT_LINE('Employee ID: ' || l_emp_ids(i));\n         END LOOP;\n      END AFTER STATEMENT;\n   END compound_trigger_example;