DECLARE\n    TYPE AttrValueRec IS RECORD (\n        attr        VARCHAR2(40),\n        val         VARCHAR2(2000),\n        inst        NUMBER(4)\n    );\n\n    attr_variable AttrValueRec;  -- Define a variable of the record type\n\n    FUNCTION create_attrval(attr   attr_variable.attr%TYPE,\n                            val    attr_variable.val%TYPE,\n                            inst   attr_variable.inst%TYPE := 1)\n    RETURN AttrValueRec IS\n        attr_value  AttrValueRec;\n    BEGIN\n        attr_value.attr := attr;\n        attr_value.val := val;\n        attr_value.inst := inst;\n        RETURN attr_value;\n    END;\nBEGIN\n    NULL;\nEND;