DECLARE\n  v_id       NUMBER;\n  v_clob     CLOB;\n  v_xml      XMLType;\nBEGIN\n  FOR rec IN (SELECT id, xmldata FROM original_table) LOOP\n    BEGIN\n      v_id := rec.id;\n      v_clob := rec.xmldata;\n\n      -- Ensure the CLOB is properly encoded\n      v_xml := XMLType(v_clob);\n      \n      -- Insert into the target table\n      INSERT INTO mytable (id, xmldata) VALUES (v_id, v_xml);\n    EXCEPTION\n      WHEN OTHERS THEN\n        -- Log the error for further analysis\n        DBMS_OUTPUT.PUT_LINE('Error processing ID: ' || v_id || ' - ' || SQLERRM);\n    END;\n  END LOOP;\nEND;\n/