FUNCTION find_customer(name VARCHAR2 DEFAULT NULL, name_set BOOLEAN DEFAULT FALSE,\n                       number VARCHAR2 DEFAULT NULL, number_set BOOLEAN DEFAULT FALSE) RETURN NUMBER IS\nBEGIN\n    -- Construct the query based on the flags\n    IF name_set AND number_set THEN\n        DBMS_OUTPUT.PUT_LINE('SELECT * FROM customer WHERE customer.name = ''' || name || ''' AND customer.number = ''' || number || '''');\n    ELSIF name_set THEN\n        DBMS_OUTPUT.PUT_LINE('SELECT * FROM customer WHERE customer.name = ''' || name || '''');\n    ELSIF number_set THEN\n        DBMS_OUTPUT.PUT_LINE('SELECT * FROM customer WHERE customer.number = ''' || number || '''');\n    ELSE\n        DBMS_OUTPUT.PUT_LINE('SELECT * FROM customer WHERE customer.name IS NULL AND customer.number IS NULL');\n    END IF;\n\n    RETURN 0; -- Return a dummy value\nEND;