DECLARE\n  l_namespace    VARCHAR2(100) := 'http://example.com/namespace';\n  l_method_name  VARCHAR2(50) := 'MyMethod';\n  l_parameters   VARCHAR2(500) := '<param1>value1</param1><param2>value2</param2>';\n  l_endpoint     VARCHAR2(200) := 'http://example.com/webservice';\n  l_soap_action  VARCHAR2(100) := 'MySoapAction';\n  l_soap_envelope CLOB;\n  l_response     CLOB;\nBEGIN\n  l_soap_envelope := web_service_util.build_soap_envelope(l_namespace, l_method_name, l_parameters);\n  l_response := web_service_util.call_web_service(l_endpoint, l_soap_action, l_soap_envelope);\n  DBMS_OUTPUT.put_line('Response: ' || l_response);\nEND;\n/