SQL> DECLARE\n  2     b1   BOOLEAN;\n  3     b2   BOOLEAN;\n  4     FUNCTION checkit RETURN BOOLEAN IS\n  5     BEGIN\n  6        dbms_output.put_line('inside checkit');\n  7        RETURN TRUE;\n  8     END checkit;\n  9  \n 10     PROCEDURE outp(n VARCHAR2, p BOOLEAN) IS\n 11     BEGIN\n 12        IF p THEN\n 13           dbms_output.put_line(n||' is true');\n 14        ELSE\n 15           dbms_output.put_line(n||' is false');\n 16        END IF;\n 17     END;\n 18  BEGIN\n 19     -- Explicitly call checkit to ensure it is evaluated\n 20     b1 := checkit OR TRUE;\n 21     outp('b1',b1);\n 22     \n 23     -- Explicitly call checkit to ensure it is evaluated\n 24     b2 := TRUE OR checkit;\n 25     outp('b2',b2);\n 26  END;\n 27  /