CREATE OR REPLACE TRIGGER new_row_trigger\n   AFTER INSERT ON your_table\n   FOR EACH ROW\n   BEGIN\n      DECLARE\n         l_response VARCHAR2(32767);\n      BEGIN\n         l_response := wsproxy_send_request();\n         -- You can log the response or handle it as needed\n      EXCEPTION\n         WHEN OTHERS THEN\n            -- Handle exceptions if needed\n            NULL;\n      END;\n   END;\n   /