UPDATE TABLE_PRODUCTS \nJOIN (\n    SELECT id\n    FROM (\n        SELECT id, \n               ROW_NUMBER() OVER (PARTITION BY products_parent_id ORDER BY id) as row_num\n        FROM TABLE_PRODUCTS\n        WHERE products_parent_id = 2\n    ) ranked\n    WHERE row_num = 3\n) target\nON TABLE_PRODUCTS.id = target.id\nSET products_quantity = products_quantity + $order['products_quantity'], \n    products_ordered = products_ordered - $order['products_quantity'];