-- Table A\nCREATE TABLE A (\n    Aid NUMBER PRIMARY KEY\n);\n\n-- Table B\nCREATE TABLE B (\n    Aid NUMBER,\n    bnum NUMBER,\n    PRIMARY KEY (Aid, bnum),\n    FOREIGN KEY (Aid) REFERENCES A(Aid)\n);\n\n-- Table C\nCREATE TABLE C (\n    Aid NUMBER,\n    cnum NUMBER,\n    PRIMARY KEY (Aid, cnum),\n    FOREIGN KEY (Aid) REFERENCES A(Aid)\n);\n\n-- Table D\nCREATE TABLE D (\n    Aid NUMBER,\n    bnum NUMBER,\n    cnum NUMBER,\n    dnum NUMBER,\n    PRIMARY KEY (Aid, bnum, cnum, dnum),\n    FOREIGN KEY (Aid, bnum) REFERENCES B(Aid, bnum),\n    FOREIGN KEY (Aid, cnum) REFERENCES C(Aid, cnum)\n);