CREATE TABLE FactCaseEvent (\n       FactID INT PRIMARY KEY,\n       CaseID INT,\n       EventTypeID INT,\n       DeptID INT,\n       TimeID INT,\n       EventDate TIMESTAMP,\n       Duration INT, -- Duration in minutes or appropriate unit\n       FOREIGN KEY (CaseID) REFERENCES DimCase(CaseID),\n       FOREIGN KEY (EventTypeID) REFERENCES DimEventType(EventTypeID),\n       FOREIGN KEY (DeptID) REFERENCES DimDepartment(DeptID),\n       FOREIGN KEY (TimeID) REFERENCES DimTime(TimeID)\n   );