DECLARE @TableName NVARCHAR(128) = 'TheTable';\nDECLARE @ColumnName NVARCHAR(128) = 'TheColumn';\nDECLARE @ConstraintName NVARCHAR(128);\nDECLARE @sql NVARCHAR(MAX);\n\n-- Find the name of the default constraint\nSELECT @ConstraintName = dc.name\nFROM sys.default_constraints dc\nJOIN sys.columns c ON dc.parent_object_id = c.object_id AND dc.parent_column_id = c.column_id\nJOIN sys.tables t ON t.object_id = c.object_id\nWHERE t.name = @TableName AND c.name = @ColumnName;\n\n-- Construct the ALTER TABLE statement to drop the constraint\nSET @sql = 'ALTER TABLE ' + QUOTENAME(@TableName) + ' DROP CONSTRAINT ' + QUOTENAME(@ConstraintName);\n\n-- Execute the constructed statement\nEXEC sp_executesql @sql;