CREATE TABLE AccountIDMapping (\n   OldAccountID INT,\n   NewAccountID INT\n);\n</cfquery>\n\n<cfquery name="populateMapping" datasource="yourDatasource">\nWITH NumberedAccounts AS (\n   SELECT accountID, ROW_NUMBER() OVER (ORDER BY accountID) AS NewAccountID\n   FROM accounts\n)\nINSERT INTO AccountIDMapping (OldAccountID, NewAccountID)\nSELECT accountID, NewAccountID\nFROM NumberedAccounts;\n</cfquery>\n\n<cfquery name="updateAccounts" datasource="yourDatasource">\nUPDATE a\nSET a.accountID = m.NewAccountID\nFROM accounts a\nJOIN AccountIDMapping m ON a.accountID = m.OldAccountID;\n</cfquery>\n\n<cfquery name="updateNotes" datasource="yourDatasource">\nUPDATE n\nSET n.accountID = m.NewAccountID\nFROM notes n\nJOIN AccountIDMapping m ON n.accountID = m.OldAccountID;\n</cfquery>\n\n<cfquery name="updateEquipment" datasource="yourDatasource">\nUPDATE e\nSET e.accountID = m.NewAccountID\nFROM equipment e\nJOIN AccountIDMapping m ON e.accountID = m.OldAccountID;\n</cfquery>\n\n<cfquery name="dropMapping" datasource="yourDatasource">\nDROP TABLE AccountIDMapping;\n</cfquery>