CREATE OR REPLACE PROCEDURE YOUR_PROCEDURE AS\nBEGIN\n  -- Your original procedure code\n  -- ...\n\n  -- Check if the job is running too long and handle the exception\n  EXCEPTION\n    WHEN OTHERS THEN\n      IF DBMS_SCHEDULER.job_exists('MY_PROCEDURE_JOB') THEN\n        DBMS_SCHEDULER.stop_job('MY_PROCEDURE_JOB', FORCE => TRUE);\n      END IF;\n      RAISE;\nEND YOUR_PROCEDURE;\n/