-- Create a user-defined table type\n   CREATE TYPE dbo.IDList AS TABLE (ID INT);\n\n   -- Create a stored procedure that takes a TVP parameter\n   CREATE PROCEDURE UpdateRecords\n       @IDs dbo.IDList READONLY,\n       @NewValue NVARCHAR(255)\n   AS\n   BEGIN\n       UPDATE t\n       SET t.Property = @NewValue\n       FROM TargetTable t\n       INNER JOIN @IDs temp ON t.ID = temp.ID;\n   END;\n\n   -- Execute the stored procedure with a TVP\n   DECLARE @IDList dbo.IDList;\n   INSERT INTO @IDList (ID) VALUES (1), (2), (3), ..., (10000);\n\n   EXEC UpdateRecords @IDList, 'NewValue';