-- Create a temporary table\n   CREATE TABLE #TempIDs (ID INT);\n\n   -- Insert IDs into the temporary table\n   INSERT INTO #TempIDs (ID) VALUES (1), (2), (3), ..., (10000);\n\n   -- Perform the update using a join with the temporary table\n   UPDATE t\n   SET t.Property = 'NewValue'\n   FROM TargetTable t\n   INNER JOIN #TempIDs temp ON t.ID = temp.ID;\n\n   -- Drop the temporary table\n   DROP TABLE #TempIDs;