BEGIN TRANSACTION;\n\nBEGIN TRY\n    -- Attempt to insert a record\n    INSERT INTO YourTable (Column1, Column2)\n    VALUES ('Value1', 'Value2');\nEND TRY\nBEGIN CATCH\n    -- Check if the error number corresponds to a duplicate key error (error number 2627 or 2601)\n    IF ERROR_NUMBER() IN (2627, 2601)\n    BEGIN\n        -- Handle the duplicate key error, e.g., log the error or simply continue\n        PRINT 'Duplicate key error encountered. Continuing execution...';\n    END\n    ELSE\n    BEGIN\n        -- If it's a different error, re-raise the error\n        THROW;\n    END\nEND CATCH;\n\n-- Continue with other SQL statements\n-- Example: Update statement\nBEGIN TRY\n    UPDATE YourTable\n    SET Column2 = 'NewValue2'\n    WHERE Column1 = 'Value1';\nEND TRY\nBEGIN CATCH\n    -- Handle any errors if necessary\n    PRINT 'Error encountered in UPDATE statement.';\n    THROW;  -- Optionally re-raise the error\nEND CATCH;\n\n-- Commit the transaction if everything is successful\nCOMMIT TRANSACTION;