CREATE TABLE MismatchedAccounts (\n    AccountNo INT,\n    BalanceTemp1 FLOAT,\n    BalanceTemp2 FLOAT\n);\n\nINSERT INTO MismatchedAccounts (AccountNo, BalanceTemp1, BalanceTemp2)\nSELECT \n    t1.AccountNo, \n    t1.Balance AS BalanceTemp1, \n    t2.Balance AS BalanceTemp2\nFROM \n    temp1 t1\nJOIN \n    temp2 t2 ON t1.AccountNo = t2.AccountNo\nWHERE \n    t1.Balance <> t2.Balance;