CREATE TABLE DimTime (\n    TimeKey INT PRIMARY KEY,\n    Date DATE,\n    Hour TINYINT,\n    DayOfWeek TINYINT,\n    Month TINYINT,\n    Year SMALLINT,\n    IsHoliday BIT\n);\n\n-- Example Fact table with a foreign key to DimTime\nCREATE TABLE FactSales (\n    SalesID INT PRIMARY KEY,\n    TimeKey INT,\n    Amount DECIMAL(10, 2),\n    FOREIGN KEY (TimeKey) REFERENCES DimTime(TimeKey)\n);