CREATE TABLE UserFunctions (\n    FunctionId INT PRIMARY KEY IDENTITY,\n    FunctionName NVARCHAR(100) NOT NULL,\n    FunctionDefinition NVARCHAR(MAX) NOT NULL, -- Store the function definition as a string\n    CreatedAt DATETIME DEFAULT GETDATE(),\n    UpdatedAt DATETIME DEFAULT GETDATE()\n);\n\nCREATE TABLE FunctionParameters (\n    ParameterId INT PRIMARY KEY IDENTITY,\n    FunctionId INT FOREIGN KEY REFERENCES UserFunctions(FunctionId),\n    ParameterName NVARCHAR(100) NOT NULL,\n    ParameterType NVARCHAR(50) NOT NULL -- e.g., 'int', 'float', etc.\n);