CREATE TABLE SequenceTable (\n    TableName NVARCHAR(128) PRIMARY KEY,\n    NextValue INT\n);\n\n-- Initialize the sequence for a specific table\nINSERT INTO SequenceTable (TableName, NextValue) VALUES ('YourTableName', 1);\n\n-- Procedure to get the next value\nCREATE PROCEDURE GetNextValue\n    @TableName NVARCHAR(128),\n    @NextValue INT OUTPUT\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    BEGIN TRANSACTION;\n    UPDATE SequenceTable\n    SET @NextValue = NextValue = NextValue + 1\n    WHERE TableName = @TableName;\n    COMMIT TRANSACTION;\nEND;