WITH VehicleAgeing AS (\n    SELECT \n        Ins_Comp,\n        dbo.fn_1To30Ageing(...) AS Ageing1To30,\n        dbo.fn_31To60Ageing(...) AS Ageing31To60,\n        dbo.fn_61To90Ageing(...) AS Ageing61To90\n    FROM YourTable\n    WHERE ...\n)\nSELECT \n    Ins_Comp,\n    Ageing1To30,\n    Ageing31To60,\n    Ageing61To90,\n    (Ageing1To30 + Ageing31To60 + Ageing61To90) AS TotalVehicles\nFROM VehicleAgeing;