-- Insert Roles\nINSERT INTO Roles (role_name) VALUES ('Admin');\nINSERT INTO Roles (role_name) VALUES ('Editor');\nINSERT INTO Roles (role_name) VALUES ('User');\n\n-- Insert Permissions\nINSERT INTO Permissions (permission_name) VALUES ('CREATE_POST');\nINSERT INTO Permissions (permission_name) VALUES ('EDIT_POST');\nINSERT INTO Permissions (permission_name) VALUES ('DELETE_POST');\n\n-- Assign Permissions to Roles\nINSERT INTO RolePermissions (role_id, permission_id) VALUES (1, 1); -- Admin can CREATE_POST\nINSERT INTO RolePermissions (role_id, permission_id) VALUES (1, 2); -- Admin can EDIT_POST\nINSERT INTO RolePermissions (role_id, permission_id) VALUES (1, 3); -- Admin can DELETE_POST\nINSERT INTO RolePermissions (role_id, permission_id) VALUES (2, 1); -- Editor can CREATE_POST\nINSERT INTO RolePermissions (role_id, permission_id) VALUES (2, 2); -- Editor can EDIT_POST\n\n-- Assign Roles to Users\nINSERT INTO UserRoles (user_id, role_id) VALUES (1, 1); -- User 1 is Admin\nINSERT INTO UserRoles (user_id, role_id) VALUES (2, 2); -- User 2 is Editor