CREATE TABLE Stories (\n    ID INT PRIMARY KEY, -- Primary key constraint\n    Title NVARCHAR(255),\n    Url NVARCHAR(255),\n    UniqueName NVARCHAR(255) UNIQUE, -- Unique constraint\n    CategoryID INT FOREIGN KEY REFERENCES Category(CategoryID)\n);\n\n-- Create clustered index on UniqueName\nCREATE CLUSTERED INDEX IX_Stories_UniqueName ON Stories (UniqueName);\n\n-- Create nonclustered index on CategoryID\nCREATE NONCLUSTERED INDEX IX_Stories_CategoryID ON Stories (CategoryID);