WITH CTE_1 AS (\n    SELECT Values, '-' AS Change\n    FROM YourTable\n    WHERE ID = 1\n    EXCEPT\n    SELECT Values, '-' AS Change\n    FROM YourTable\n    WHERE ID = 2\n),\nCTE_2 AS (\n    SELECT Values, '+' AS Change\n    FROM YourTable\n    WHERE ID = 2\n    EXCEPT\n    SELECT Values, '+' AS Change\n    FROM YourTable\n    WHERE ID = 1\n)\nSELECT Values, Change\nFROM CTE_1\nUNION ALL\nSELECT Values, Change\nFROM CTE_2\nORDER BY Values;