CREATE PROCEDURE GetCubeData\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    -- Replace 'YourLinkedServerName' with the name of your linked server\n    -- Replace 'YourMDXQuery' with your actual MDX query\n    DECLARE @mdxQuery NVARCHAR(MAX);\n    SET @mdxQuery = N'\n        SELECT \n            { [Measures].[YourMeasure] } ON COLUMNS,\n            { [Dimension].[Hierarchy].Members } ON ROWS\n        FROM \n            [YourCubeName]\n        WHERE \n            ( [Dimension].[Attribute].[YourAttribute] = "SomeValue" )';\n\n    EXEC sp_executesql N'\n        SELECT * \n        FROM OPENQUERY([YourLinkedServerName], @mdxQuery)',\n        N'@mdxQuery NVARCHAR(MAX)',\n        @mdxQuery = @mdxQuery;\nEND