SELECT \n    days_of_months.Date,\n    m.NAME as Model,\n    COUNT(t.ID) as Count\nFROM \n    (SELECT DATE(CONCAT(2009, '-', temp_months.id, '-', temp_days.id)) as Date\n     FROM temp_months\n     INNER JOIN temp_days ON temp_days.id <= temp_months.last_day\n     WHERE temp_months.id = 3 -- March\n    ) days_of_months\nCROSS JOIN MODEL m\nLEFT JOIN APPLIANCE_UNIT a ON m.ID = a.MODEL_FK AND a.NUMBER_OF_UNITS > 0\nLEFT JOIN NEW_TICKET t ON a.NEW_TICKET_FK = t.ID AND t.TYPE = 'SALES' AND t.SALES_ORDER_FK IS NOT NULL AND DATE(t.CREATION_DATE_TIME) = days_of_months.Date\nGROUP BY \n    days_of_months.Date,\n    m.ID, m.NAME\nORDER BY \n    days_of_months.Date, m.NAME;