SELECT \n    CONCAT(u.firstname, ' ', u.lastname) AS name, \n    u.id, \n    s.description, \n    s.shiftstart, \n    s.shiftend, \n    SUM(TIME_TO_SEC(TIMEDIFF(shiftend, shiftstart)) / 3600) AS total\nFROM \n    shifts s\nINNER JOIN \n    users u ON s.id = u.id\nWHERE \n    s.id = '$user'\n    AND DATE(shiftstart) BETWEEN '$start' AND '$end'\nGROUP BY \n    u.id, s.description, s.shiftstart, s.shiftend WITH ROLLUP\nORDER BY \n    shiftstart;