DECLARE\n  TYPE ref_cursor IS REF CURSOR;\n  cur ref_cursor;\n  rec_count INTEGER := 0;\n  dummy_table YOUR_TABLE_NAME%ROWTYPE; -- Replace YOUR_TABLE_NAME with your actual table name\nBEGIN\n  -- Open the reference cursor (modify the query as needed)\n  OPEN cur FOR SELECT * FROM YOUR_TABLE_NAME; -- Replace YOUR_TABLE_NAME with your actual table name\n\n  -- Fetch each row and count\n  LOOP\n    FETCH cur INTO dummy_table;\n    EXIT WHEN cur%NOTFOUND;\n    rec_count := rec_count + 1;\n  END LOOP;\n\n  -- Close the cursor\n  CLOSE cur;\n\n  -- Output the record count\n  DBMS_OUTPUT.PUT_LINE('Record Count: ' || rec_count);\nEND;\n/